@extends('layouts.app')
@include('components.web.top-nav-bar')
@include('components.web.side-nav-bar',['menuActive' => 'overview'])

@section('head')
@parent
<style>
    .overview-layout-container {
        pointer-events: none;
        margin-top: 0.3rem;
    }

    .overview-layout-container .work-center,
    .overview-layout-container .factory {
        pointer-events: all;
        cursor: pointer;
        transition-duration: 250ms;
    }

    .overview-layout-container .work-center:hover,
    .overview-layout-container .factory:hover {
        filter: brightness(1.5);

    }

    .overview-layout-container svg {
        width:100%;
    }

    .info-label {
        font-size: 0.7rem;
    }

    .info-text {
        color: #000080;
    }

    .info-item {
        display: flex;
    }

    .info-detail {
        flex: 1;
        font-weight: 600;
    }

    .info-icon {
        font-size: 1.8em;
        padding: 0 0.4em 0 0.4em;
    }

    .status {
        width: 650px;
    }

    .work-center rect {
        transition: fill 250ms linear;
        -moz-transition: fill 250ms linear;
        -webkit-transition: fill 250ms linear;
        -ms-transition: fill 250ms linear;
    }

    /* idle status */
    .work-center-state-idle rect {
        fill: #fff;
    }

    .work-center-state-idle path {
        fill: #626162;
    }

    /* running status */
    .work-center-state-running rect {
        fill: #03941A;
    }

    .work-center-state-running path {
        fill: #fff;
    }

    /* die change status */
    .work-center-state-plan-die-change rect {
        fill: #ffeb3b;
    }

    .work-center-state-plan-die-change path {
        fill: #000;
    }

    .work-center-state-unplan-die-change rect {
        animation: blink-animation-unplan-die-change 1s infinite alternate;
        -webkit-animation: blink-animation-unplan-die-change 1s infinite alternate;
    }

    .work-center-state-unplan-die-change path {
        fill: #000;
    }

    /* break status */
    .work-center-state-break rect {
        fill: #800080;
    }

    .work-center-state-break path {
        fill: #fff;
    }

    /* human downtime status */
    .work-center-state-human-downtime rect {
        animation: blink-animation-human-downtime 1s infinite alternate;
        -webkit-animation: blink-animation-human-downtime 1s infinite alternate;
    }

    .work-center-state-human-downtime path {
        fill: white;
    }
    @keyframes blink-animation-unplan-die-change {
        0% {
            fill: #ffa000;
        }

        100% {
            fill: #ffd000;
        }
    }

    @keyframes blink-animation-human-downtime {
        0% {
            fill: #303F9F;
        }

        100% {
            fill: #0000FF;
        }
    }

    @-webkit-keyframes blink-animation-human-downtime {
        0% {
            fill: #303F9F;
        }

        100% {
            fill: #0000FF;
        }
    }

    /* machine downtime status */
    .work-center-state-machine-downtime rect {
        animation: blink-animation-machine-downtime 1s infinite alternate;
        -webkit-animation: blink-animation-machine-downtime 1s infinite alternate;
        transition: fill 250ms linear;
        -moz-transition: fill 250ms linear;
        -webkit-transition: fill 250ms linear;
        -ms-transition: fill 250ms linear;
    }

    .work-center-state-machine-downtime path {
        fill: #fff;
    }

    @keyframes blink-animation-machine-downtime {
        0% {
            fill: #C62828;
        }

        100% {
            fill: #FF073A;
        }
    }

    @-webkit-keyframes blink-animation-machine-downtime {
        0% {
            fill: #C62828;
        }

        100% {
            fill: #FF073A;
        }
    }

    @media only screen and (max-width: 768px) {
        .status {
            margin-top: 2rem 0;
            width: 530px;
        }
    }

    @media only screen and (max-width: 560px) {
        .status {
            width: 450px;
        }
    }

    @media only screen and (max-width: 480px) {
        .status {
            width: 380px;
        }
    }

    .marlay {
        margin-top: -3rem;
    }

    .marin {
        margin-top: 5rem;
    }
</style>
@endsection

