{{-- work center const status state --}}
<script>
    /** Work Center Idle */
    const STATUS_IDLE = 0;
    /** Work Center Die Change */
    const STATUS_DIE_CHANGE = 1;
    /** Work Center First Product Confirmation */
    const STATUS_FIRST_CONFIRMATION = 2;
    /** Work Center Running */
    const STATUS_RUNNING = 3;

    /** No Downtime */
    const DOWNTIME_STATUS_NONE = 0;

    /** Unplanned Downtime: Human */
    const DOWNTIME_STATUS_UNPLAN_HUMAN = -1;
    /** Unplanned Downtime: Machine */
    const DOWNTIME_STATUS_UNPLAN_MACHINE = -2;
    /** Unplanned Downtime: Die-Change */
    const DOWNTIME_STATUS_UNPLAN_DIE_CHANGE = -3;

    /** Planned Downtime: Die-Change */
    const DOWNTIME_STATUS_PLAN_DIE_CHANGE = 3;
    /** Planned Downtime: Break */
    const DOWNTIME_STATUS_PLAN_BREAK = 4;
</script>

{{-- web socket, initialize overview layout, initiliaze tag --}}
<script>
    $(() => {
        overviewLayout.initialize();

        LivePage.listenChanges(
            '*', //work center id( * for all)
            'work-center-data', //class
            {
                tag: '_status'
            },
            updateWorkCenterState
        );

        LivePage.forceUpdate();
    });

    function updateWorkCenterState(config, value, summary) {
        let workCenterUid = summary.work_center.uid;

        let workCenter = $('.work-center[data-work-center-uid="' + workCenterUid + '"]');

        if (workCenter.length == 0) {
            console.log('work center not found');
            return;
        }

        let status = value.status;
        let downtimeState = value.downtime_state;

        workCenter.removeClass('work-center-state-running');
        workCenter.removeClass('work-center-state-human-downtime');
        workCenter.removeClass('work-center-state-machine-downtime');
        workCenter.removeClass('work-center-state-plan-die-change');
        workCenter.removeClass('work-center-state-unplan-die-change');

        workCenter.removeClass('work-center-state-break');
        workCenter.removeClass('work-center-state-idle');

        if (status == STATUS_IDLE) {
            workCenter.addClass('work-center-state-idle');
        } else {
            switch (downtimeState) {
                case DOWNTIME_STATUS_NONE:
                    workCenter.addClass('work-center-state-running');
                    console.log('work center running');
                    break;
                case DOWNTIME_STATUS_PLAN_BREAK:
                    workCenter.addClass('work-center-state-break');
                    console.log('work center break');
                    break;
                case DOWNTIME_STATUS_PLAN_DIE_CHANGE:
                    workCenter.addClass('work-center-state-plan-die-change');
                    console.log('work center plan die change');
                    break;
                case DOWNTIME_STATUS_UNPLAN_DIE_CHANGE:
                    workCenter.addClass('work-center-state-unplan-die-change');
                    console.log('work center unplan die change');
                    break;
                case DOWNTIME_STATUS_UNPLAN_HUMAN:
                    workCenter.addClass('work-center-state-human-downtime');
                    console.log('work center human downtime');
                    break;
                case DOWNTIME_STATUS_UNPLAN_MACHINE:
                    workCenter.addClass('work-center-state-machine-downtime');
                    console.log('work center machine downtime');
                    break;
            }
        }
    }

    var overviewLayout = {
        baseUrlWorkCenterClick: "{{ route('realtime.oee.index',[$plant->uid,'__uid__']) }}",
        baseUrlFactoryClick: "{{ route('realtime.factory-oee.index',[$plant->uid,'__uid__']) }}",
        initialize: function() {
            $('.work-center').click(this.onClickWorkCenter);
            $('.factory').click(this.onClickFactory);
        },
        onClickWorkCenter: function(sender) {

            let uid = $(this).data('work-center-uid');
            console.log('tekan', uid, sender);
            if (typeof(uid) === 'undefined')
                return;

            window.location.href = overviewLayout.baseUrlWorkCenterClick.replace('__uid__', uid);

        },
        onClickFactory: function(sender) {
            let uid = $(sender.target).data('uid');
            if (typeof(uid) === 'undefined')
                return;

            window.location.href = overviewLayout.baseUrlFactoryClick.replace('__uid__', uid);
        },
    };
</script>
@endsection
